mapper.insert:
INSERT INTO board(num, writer, email, subject, reg_date,readcount, ref, re_step, re_level, content, ip, upload) VALUES(board_num_seq.nextval, #{writer, jdbcType=VARCHAR}, #{email, jdbcType=VARCHAR}, #{subject, jdbcType=VARCHAR}, sysdate, 0,
board_num_seq.nextval #{ref}
, #{re_step}, #{re_level}, #{content, jdbcType=VARCHAR}, #{ip}, #{upload, jdbcType=VARCHAR})

mapper.update:
UPDATE board
SET readcount =	readcount + 1
WHERE num=#{num}

mapper.select:
SELECT upload
FROM board
WHERE num=#{num}

mapper.update:
UPDATE board
SET re_step=re_step + 1
WHERE ref=#{ref} AND re_step>#{re_step}

UPDATE board
SET subject=#{subject}, email=#{email}, content=#{content}

,upload=#{upload} 

WHERE num=#{num}
